import React, { useReducer } from "react";
import CardContext from "./card-context";

const defaultCartProvider = {
  items: [],
  totalAmount: 0,
};

const cartReducer = (state, action) => {
    if(action.type === 'ADD'){
        const updatedItems = state.items.concat(action.item);
        const updatedTotalAmount = state.totalAmount + action.item.price * action.item.amount;

        return {
            items: updatedItems,
            totalAmount: updatedTotalAmount
        }
    }
  return defaultCartProvider;
};

const CardProvider = (props) => {
  const [cartState, dispatchedCartAction] = useReducer(
    cartReducer,
    defaultCartProvider
  );

  const addItemstoCartHandler = (item) => {
    dispatchedCartAction({type: 'ADD' , item: item})
  };
  const removeItemstoCartHandler = (id) => {
    dispatchedCartAction({type: 'REMOVE' , id: id})
  };

  const cartContext = {
    items: [],
    totalAmount: 0,
    addItems: addItemstoCartHandler,
    removeItems: removeItemstoCartHandler,
  };

  return (
    <CardContext.Provider value={cartContext}>
      {props.children}
    </CardContext.Provider>
  );
};

export default CardProvider;
